import { Component, OnInit } from '@angular/core';
import { Subscription } from 'rxjs';
import { PostServiceService } from '../post-service.service';

@Component({
  selector: 'app-post-display',
  templateUrl: './post-display.component.html',
  styleUrls: ['./post-display.component.css']
})
export class PostDisplayComponent implements OnInit {

  posts:{_id:string, title:string, description:string, departmentCode: string, __v: string}[] = []

  constructor(public postService: PostServiceService) {}

  private postsubscription!: Subscription

ngOnInit() {
  this.postService.getpost_service()
  this.postsubscription = this.postService.getUpdateListener()
  .subscribe((posts:{_id:string, title:string, description:string, departmentCode: string, __v: string}[])=>
  {
    this.posts = posts
  })
}

ngOnDestroy()
{
  this.postsubscription.unsubscribe()
}

ngOnDelete(postid: string)
{
  this.postService.deletepost_service(postid)
}

}
<p class="screenTitle">All Posts</p>
<p class = "subtext">Below are all the posts made by the community</p>
<hr/>
<div class="postDisplay" *ngFor="let post of posts">
<p class="postTitle">Title:</p>
{{post.title}} 
<p class="postDescription">Description:</p>
{{post.description}}
<p class="postDepartmentCode">Department Code:</p>
{{post.departmentCode}}
<br>
<br>
<button (click)="ngOnDelete(post._id)">Delete</button>
<hr/>
</div>
