import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http'
import { AuthServiceService } from '../auth/auth-service.service';
import { Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PostServiceService {

private updatedpostsdisplay = new Subject<{_id:string,title:string,description:string,departmentCode: string,__v: string}[]>();

//private postsdisplay:{_id:string, title:string,description:string,departmentCode: string,__v: string}[] = [];

private postsdisplay: { _id: string, title: string, description: string, departmentCode: string, __v: string }[] = [
  {
    _id: '1',
    title: 'Post 1',
    description: 'Description 1',
    departmentCode: 'Dept1',
    __v: '1',
  },
  {
    _id: '2',
    title: 'Post 2',
    description: 'Description 2',
    departmentCode: 'Dept2',
    __v: '2',
  }
];

/*
  {
      "_id": "65256af470244bb342eb883e",
      "title": "my first post",
      "description": "my first description",
      "departmentCode": "A100",
      "__v": 0
  }
  */


constructor(private http: HttpClient) { }


addpost_service(ptitle: string, pdescription: string, pdepartmentcode: string )
{
  this.http.post<{message:string, post:any}>('https://localhost:3000/api/posts', {title:ptitle, description:pdescription, departmentCode:pdepartmentcode})
  .subscribe((thepost)=>
  {
this.postsdisplay.push(thepost.post)
this.updatedpostsdisplay.next([...this.postsdisplay])
  })
}


getpost_service()
{
  this.http.get<{message:string,posts:any}>('https://localhost:3000/api/posts')
  .subscribe((thepost)=>
  {
    alert(thepost)
    //this.postsdisplay = thepost.posts;
    this.updatedpostsdisplay.next([...this.postsdisplay]);


  })
}


deletepost_service(postid: string)
{
  this.http.delete('https://localhost:3000/api/posts/' + postid)
  .subscribe(()=>
  {
    const updatedpostsdeleted = this.postsdisplay.filter(post => post._id!==postid)
    this.postsdisplay = updatedpostsdeleted
    this.updatedpostsdisplay.next([...this.postsdisplay])
  })
}

getUpdateListener()
{
  return this.updatedpostsdisplay.asObservable()
}


}
